// --- Limpieza de texto procedente de PDF ---
let texto = $input.first().json.text;

// 2️⃣ Elimina números de página ("Página X de Y")
texto = texto.replace(/Página\s+\d+\s+de\s+\d+/gi, "");

// 4️⃣ Elimina líneas en blanco múltiples
texto = texto.replace(/\n\s*\n+/g, "\n");

// 5️⃣ Elimina guiones o símbolos repetitivos
texto = texto.replace(/[-–•]{2,}/g, "");

// 6️⃣ Corrige espacios duplicados
texto = texto.replace(/\s{2,}/g, " ");

// 🔹 Eliminar puntos suspensivos, guiones o signos repetidos
texto = texto.replace(/[•·–—\-]{2,}/g, "");
texto = texto.replace(/\.+/g, ".");

// 7️⃣ Corta partes redundantes (como índices o pies de página)
texto = texto.replace(/ÍNDICE[\s\S]*?CAPÍTULO\s+I/gi, "CAPÍTULO I");

// 8️⃣ (Opcional) Corta longitud si excede 60.000 caracteres
if (texto.length > 60000) {
  texto = texto.slice(0, 60000);
}

return {
  json: {
    tipo: $json.tipo,
    fileName: $json.fileName,
    text_clean: texto
  }
};

